    private fun getSampleImageBitmap(
        base64String: String =
            ""
    ): ImageBitmap {
//        val base64String =
//            ""
        val imageBytes = Base64.decode(base64String, Base64.DEFAULT)
        val decodedImage = BitmapFactory.decodeByteArray(imageBytes, 0, imageBytes.size)
        return decodedImage.asImageBitmap()


    }


